mapper.update:
UPDATE TBS_APPROVAL SET
	 FG_DEL='Y'
	 ,TP_STATUS_APPR='04' 
	 ,ID_EMP_UPT=#{user.id}
	 ,ID_DEPT_UPT=#{user.partCode}
	 ,DT_UPT=SYSDATE

	 WHERE ID_APPR=#{ID_APPR}

UPDATE TBS_APPROVAL_DTL SET 
	FG_DEL='Y' 
	,ID_EMP_UPT=#{user.id}
	,ID_DEPT_UPT=#{user.partCode}
	,DT_UPT=SYSDATE

	WHERE ID_APPR=#{ID_APPR}

		UPDATE TBS_APPROVAL_DTL
		   SET
      	  	     DS_COMMENT = #{DS_COMMENT}
      	  	   , TP_APPR_PROC = '04'

      	  	   , DT_ACT = SYSDATE               	  	   
      	  	   , ID_EMP_UPT = #{user.id}  
			   , ID_DEPT_UPT = #{user.partCode} 
			   , DT_UPT = SYSDATE	             				   
		 WHERE
		       ID_APPR_DTL=#{ID_APPR_DTL}

		UPDATE TBS_APPROVAL_DTL
		   SET
      	  	     DS_COMMENT = #{DS_COMMENT}
      	  	   , TP_APPR_PROC = '03'	  
      	  	   , DT_ACT = SYSDATE         	

      	  	   , ID_EMP_UPT = #{user.id}  
			   , ID_DEPT_UPT = #{user.partCode} 
			   , DT_UPT = SYSDATE	                 	  	     	   
		 WHERE
		       ID_APPR_DTL = TO_NUMBER(#{ID_APPR_DTL})

		UPDATE TBS_APPROVAL
		   SET
      	  	     TP_STATUS_APPR = '02'
      	  	   , ID_EMP_UPT = #{user.id}        
			   , ID_DEPT_UPT = #{user.partCode} 
			   , DT_UPT = SYSDATE	  
			   , DT_APPR=SYSDATE     				   	  	     	   
		 WHERE
		       ID_APPR=#{ID_APPR}

		UPDATE TBS_APPROVAL_DTL
		   SET
				 FG_USE = 'Y'
			   , DT_ARRIVAL = SYSDATE    				   
      	  	   , ID_EMP_UPT = #{user.id} 
			   , ID_DEPT_UPT = #{user.partCode} 
			   , DT_UPT = SYSDATE	             

		 WHERE
		       SEQ_APPR=#{SEQ_APPR}+1 AND ID_APPR=#{ID_APPR}

		UPDATE TBS_APPROVAL_DTL
		   SET
      	  	     DS_COMMENT = #{DS_COMMENT}
      	  	   , TP_APPR_PROC = '05'	  
      	  	   , DT_ACT = SYSDATE         	

      	  	   , ID_EMP_UPT = #{user.id}  
			   , ID_DEPT_UPT = #{user.partCode} 
			   , DT_UPT = SYSDATE	                 	  	     	   
		 WHERE
		       ID_APPR_DTL=#{ID_APPR_DTL}

