mapper.select:
SELECT CODE /* 코드 */ , CODE_NAME /* 코드명 */ , RES_ID /* 리소스ID */ , CODE_DS /* 상세코드명 */ , SORT_ORDER /* 정렬순서 */ , PARENT_CODE /* 상위코드 */ , STATUS /* 상태 */ , CODE_CATEGORY_ID /* 코드카테고리ID */ FROM SYS_CODE WHERE 1=1
AND CODE_CATEGORY_ID = #{CODE_CATEGORY_ID}
AND CODE = #{CODE}
ORDER BY SORT_ORDER

SELECT CODE_GROUP_ID /* 코드그룹ID */ , CODE_GROUP_NM /* 코드그룹명 */ FROM SYS_CODE_GROUP WHERE 1=1
AND CODE_CATEGORY_ID = #{CODE_CATEGORY_ID}
GROUP BY CODE_GROUP_ID, CODE_GROUP_NM

mapper.insert:
		INSERT INTO SYS_CODE_GROUP(
			      CODE_GROUP_ID     /* 코드그룹번호*/
				, CODE_CATEGORY_ID  /* 코드분류번호*/
				, CODE    			/* 코드*/
				, CODE_GROUP_NM     /* 코드그룹명*/
    	)
		SELECT #{CODE_GROUP_ID},
			   CODE_CATEGORY_ID,
			   CODE,
	           #{CODE_GROUP_NM}
		FROM SYS_CODE
		WHERE CODE_CATEGORY_ID = #{CODE_CATEGORY_ID}

			AND CODE = #{CODE}


		    AND CODE IN

		   		#{CODE_ITEM}

mapper.delete:
		DELETE FROM SYS_CODE_GROUP
        WHERE CODE_CATEGORY_ID = #{CODE_CATEGORY_ID}
        AND CODE_GROUP_ID = #{GROUP_ID}
