mapper.sql:
select F.* , decode(FUNCTION_TYPE, 'SE', SERVICE_NAME, SCREEN_NAME) MODULE_NAME from ( select FNC.FUNCTION_ID AS FUNCTION_ID , FNC.FUNCTION_NAME AS FUNCTION_NAME , FNC.FUNCTION_TYPE AS FUNCTION_TYPE , FNC.DESCRIPTION AS DESCRIPTION , FNC.PROJECT_CODE AS PROJECT_CODE , FNC.CATEGORY_CODE AS CATEGORY_CODE , FNC.SERVICE_ID AS SERVICE_ID , decode(FNC.SERVICE_ID, null, '', '[' || FNC.SERVICE_ID || '] ' || SVC.SERVICE_NAME) AS SERVICE_NAME , FNC.SCREEN_ID AS SCREEN_ID , decode(FNC.SCREEN_ID, null, '', '[' || FNC.SCREEN_ID || '] ' || SCR.SCREEN_NAME) AS SCREEN_NAME , FNC.REG_USER_ID AS REG_USER_ID , FNC.REG_DATETIME AS REG_DATETIME , FNC.UPD_USER_ID AS UPD_USER_ID , FNC.UPD_DATETIME AS UPD_DATETIME , NVL(ASF.CNT, 0) AS SCREEN_MAPPING_CNT from AP_FUNCTION FNC , AP_SERVICE SVC , AP_SCREEN SCR , (SELECT FUNCTION_ID , COUNT(SCREEN_ID) AS CNT FROM AP_SCREEN_FUNCTION GROUP BY FUNCTION_ID) ASF where 1=1 AND FNC.PROJECT_CODE = #{PROJECT_CODE} and FNC.SERVICE_ID = SVC.SERVICE_ID (+) and FNC.SCREEN_ID = SCR.SCREEN_ID (+) and FNC.FUNCTION_ID = ASF.FUNCTION_ID (+)
and FNC.FUNCTION_ID like ('%' || UPPER(#{FUNCTION_ID}) || '%')
and FNC.FUNCTION_NAME like ('%' || #{FUNCTION_NAME} || '%')
and FNC.CATEGORY_CODE = #{CATEGORY_CODE}
and FNC.FUNCTION_TYPE = #{FUNCTION_TYPE}
and FNC.SERVICE_ID like ('%' || UPPER(#{SEARCH_SERVICE}) || '%')
and FNC.SCREEN_ID like ('%' || UPPER(#{SEARCH_SCREEN}) || '%')
) F order by REG_DATETIME DESC

mapper.insert:

            SELECT 'FNC'||#{CATEGORY_CODE}||LPAD(MAX(maxKey), 4, '0')
              FROM (
                SELECT replace(FUNCTION_ID, 'FNC' || #{CATEGORY_CODE}, '') +1 AS maxKey
                  FROM AP_FUNCTION
                 WHERE FUNCTION_ID like 'FNC' || #{CATEGORY_CODE} || '%'
                 UNION
                SELECT 0 AS maxKey FROM DUAL 
                   ) A

		insert into	AP_FUNCTION (
			  FUNCTION_ID
			, FUNCTION_NAME
			, FUNCTION_TYPE
			, DESCRIPTION
			, PROJECT_CODE
			, CATEGORY_CODE
			, SERVICE_ID
			, SCREEN_ID
			, REG_USER_ID
			, REG_DATETIME
			, UPD_USER_ID
			, UPD_DATETIME
		)
		values (
			  #{FUNCTION_ID}
			, #{FUNCTION_NAME}
			, #{FUNCTION_TYPE}
			, #{DESCRIPTION}
			, #{PROJECT_CODE}
			, #{CATEGORY_CODE}
			, #{SERVICE_ID}
			, #{SCREEN_ID}
			, #{user.id}
			, CURRENT_TIMESTAMP
			, #{user.id}
			, CURRENT_TIMESTAMP
		)

mapper.update:
update AP_FUNCTION
   set FUNCTION_NAME = #{FUNCTION_NAME}
     , FUNCTION_TYPE = #{FUNCTION_TYPE}
     , DESCRIPTION   = #{DESCRIPTION}
     , SERVICE_ID    = #{SERVICE_ID}
     , SCREEN_ID     = #{SCREEN_ID}
     , UPD_USER_ID   = #{user.id}
     , UPD_DATETIME  = CURRENT_TIMESTAMP
 where FUNCTION_ID   = #{FUNCTION_ID}

mapper.insert:
 	insert into	AP_FUNCTION_ALIAS (
		  FUNCTION_ALIAS_ID
		, FUNCTION_ALIAS_NAME
		, SORT_ORDER
		, REG_USER_ID
		, REG_DATETIME
		, UPD_USER_ID
		, UPD_DATETIME
	)
	values (
		  #{FUNCTION_ALIAS_ID}
		, #{FUNCTION_ALIAS_NAME}
		, #{SORT_ORDER}
		, #{user.id}
		, CURRENT_TIMESTAMP
		, #{user.id}
		, CURRENT_TIMESTAMP
	)

mapper.update:
update AP_FUNCTION_ALIAS
   set FUNCTION_ALIAS_NAME = #{FUNCTION_ALIAS_NAME}
     , SORT_ORDER          = #{SORT_ORDER:INTEGER}
     , UPD_USER_ID         = #{user.id}
     , UPD_DATETIME        = CURRENT_TIMESTAMP
 where FUNCTION_ALIAS_ID   = #{FUNCTION_ALIAS_ID}

mapper.select:
SELECT COUNT(SCREEN_ID) AS SCREEN_COUNT
	FROM AP_SCREEN_FUNCTION
	WHERE FUNCTION_ALIAS_ID = #{FUNCTION_ALIAS_ID}

mapper.delete:
DELETE
  FROM AP_SCREEN_FUNCTION
 WHERE SCREEN_ID = #{SCREEN_ID}
   AND FUNCTION_ID = #{FUNCTION_ID}
