mapper.insert:
INSERT INTO CRMADMIN.TBS_VOC_MASTER
(
	   ID_VOC                    /* VOC ID */
	 , BIZ_CHNN                  /* 접수채널 */
	 , ID_BIZ_KEY                /* 업무별ID */
	 , ORDER_NO                  /* 주문번호 */
	 , ID_STORE                  /* 매장ID */
	 , FG_MEMBER                 /* 회원여부 */
	 , ID_MEMBER                 /* 회원PK */
	 , NM_CUST                   /* 이름 */
	 , NO_PHONE                  /* 연락처 */
	 , DS_EMAIL                  /* 이메일 */
	 , YN_PRIVACY_AGREE          /* 개인정보수집동의여부 */
	 , YN_SENSITIVE_AGREE        /* 민감정보수집동의여부 */
	 , YN_PRIVACY_ORDER_AGREE    /* 개인정보제3자제공동의여부 */
	 , YN_SENSITIVE_ORDER_AGREE  /* 민감정보제3자제공동의여부 */
	 , CD_VOC_STATUS             /* VOC상태 */
	 , CD_ACT_CAUSE              /* 처리결과유형 */
	 , DS_ACT_CONT               /* 처리내용 */
	 , DT_VOC_ACT                /* 처일일시 */
	 , ID_ACT_EMP                /* 처리자 */
	 , ID_ACT_DEPT               /* 처리부서 */
	 , CD_VOC_CAUSE              /* VOC접수유형 */
	 , DS_TITLE                  /* 제목 */
	 , DS_CONT                   /* 내용 */
	 , DS_DISPATCH_CONT          /* 통보내용 */
	 , FG_DEL                    /* 삭제여부 */
	 , DT_REG                    /* 최초등록일시 */
	 , ID_REG_USER               /* 최초등록자 */
	 , DT_UPT                    /* 최근수정일시 */
	 , ID_UPT_USER               /* 최근수정자 */
	 , DT_VOC_REG                /* VOC 발생일 */
	 , MENU_CD                   /* 메뉴 코드 */
	 , PAYMENT_TYPE              /* 결제 방법 */
	 , PAYMENT_PRC               /* 결제 금액 */
	 , DT_ORDER                  /* 주문일자 */
	 , MENU_NM                   /* 메뉴명 */
	 , CD_STATUS                 /* 회원상태 */
	 , POS_ORDER_NO              /* POS 주문번호 */
	 , OMNI_ORDER_NO             /* OMNI 주문번호 */
	 , CALL_ORDER_NO             /* CALL 주문번호 */
	 , FG_SEND
) VALUES (
       #{ID_VOC}                                                      
	 , #{BIZ_CHNN}
	 , #{ID_BIZ_KEY}
	 , #{ORDER_NO}
	 , #{ID_STORE}
	 , #{FG_MEMBER}
	 , #{ID_MEMBER}
	 , #{NM_CUST}
	 , #{NO_PHONE}
	 , #{DS_EMAIL}
	 , #{YN_PRIVACY_AGREE}
	 , #{YN_SENSITIVE_AGREE}
	 , #{YN_PRIVACY_ORDER_AGREE}
	 , #{YN_SENSITIVE_ORDER_AGREE}
	 , #{CD_VOC_STATUS}
	 , #{CD_ACT_CAUSE}
	 , #{DS_ACT_CONT}
	 , DECODE(#{CD_VOC_STATUS}, '02', SYSDATE, NULL)
	 , DECODE(#{CD_VOC_STATUS}, '02', #{ID_REG_USER}, NULL)
	 , #{ID_ACT_DEPT}
	 , #{CD_VOC_CAUSE}
	 , #{DS_TITLE}
	 , #{DS_CONT}
	 , #{DS_DISPATCH_CONT}
	 , 'N'
	 , SYSDATE
	 , #{ID_REG_USER}
	 , SYSDATE
	 , #{ID_UPD_USER}
	 , TO_DATE(#{DT_VOC_REG}, 'YYYY-MM-DD')
	 , NULL
	 , #{PAYMENT_TYPE}
	 , #{PAYMENT_PRC}
	 , TO_DATE(#{DT_ORDER}, 'YYYY-MM-DD HH24:MI:SS')
	 , #{MENU_NM}
	 , #{CD_STATUS}
	 , #{POS_ORDER_NO}
	 , #{OMNI_ORDER_NO}
	 , #{CALL_ORDER_NO}
	 , 'N'
)

INSERT INTO CRMADMIN.TBS_VOC_ATTACH
(
	   ID_VOC                  /* VOC ID */
	 , FILE_NM                 /* 파일명 */
	 , FILE_KEY                /* 파일키 */
) VALUES (
       #{ID_VOC}                                                      
	 , #{FILE_NM}
	 , 'images/voc/' || #{FILE_KEY}
)

mapper.update:
		UPDATE CRMADMIN.TBS_VOC_MASTER
		   SET DT_UPT = SYSDATE
		     , ID_UPT_USER = #{ID_UPD_USER}
		
		     , CD_ACT_CAUSE = #{CD_ACT_CAUSE}
		
		
		     , DS_ACT_CONT = #{DS_ACT_CONT}
		
		
		     , CD_VOC_CAUSE = #{CD_VOC_CAUSE}
		
		
		     , DT_VOC_ACT = DECODE(#{CD_VOC_STATUS}, '02', SYSDATE, NULL)
		     , ID_ACT_EMP = DECODE(#{CD_VOC_STATUS}, '02', #{ID_UPD_USER}, NULL)
			 , CD_VOC_STATUS = #{CD_VOC_STATUS}
		
		 WHERE ID_VOC = #{ID_VOC}
