mapper.sql:
SELECT DISTINCT OMA.CMP_CD , OMA.SALES_ORG_CD , OMA.MENU_TYPE , OMA.MENU_CD , CASE WHEN OMO_MAIN.MENU_NM IS NULL THEN '' ELSE OMO_MAIN.MENU_NM||'>'||OMO_SUB.MENU_NM END AS MAIN_MENU_NM , OMA.MENU_NM , OMA.MENU_EN_NM , OMA.MENU_DESC , OMA.MENU_CMPNN , OMA.ORIGIN_INFO , OMA.MENU_IMG_PATH , OMA.SET_YN , OMA.DRINK_CHANGE_YN , OMA.SIDE_CHANGE_YN , OMA.CONDIMENT_CHANGE_YN , OMA.OPTION_RECOMMEND_YN , OMA.USE_YN , OMA.L_SET_YN , OMA.MANAGE_TYPE , TO_CHAR(OMO.REG_DATE, 'YYYY-MM-DD') AS REG_DATE , MMP.DINE_IN_PRC FROM OMNI.OMNI_MENU_APP OMA LEFT OUTER JOIN OMNI.OMNI_MENU_ORG OMO ON OMA.MENU_CD = OMO.MENU_CD AND OMO.DEPTH = 3 LEFT OUTER JOIN OMNI.OMNI_MENU_ORG OMO_SUB ON OMO.UP_MENU_CD = OMO_SUB.MENU_CD AND OMO_SUB.DEPTH = 2 LEFT OUTER JOIN OMNI.OMNI_MENU_ORG OMO_MAIN ON OMO_SUB.UP_MENU_CD = OMO_MAIN.MENU_CD AND OMO_MAIN.DEPTH = 1 LEFT OUTER JOIN OMNI.MST_MENU_PRC MMP ON MMP.MENU_CD = OMA.MENU_CD AND MMP.FNSH_DT = '99991231' WHERE 1=1
AND OMA.USE_YN = #{USE_YN}
AND OMA.MANAGE_TYPE = #{MANAGE_TYPE}
AND OMA.MENU_TYPE = #{MENU_TYPE}
AND OMO_MAIN.MENU_CD = #{MENU_CATEGORY_1}
AND OMO_SUB.MENU_CD = #{MENU_CATEGORY_2}
AND OMA.MENU_CD LIKE '%'||#{MENU_CD}||'%'
AND OMA.MENU_NM LIKE '%'||#{MENU_NM}||'%'
ORDER BY OMA.MENU_NM ASC

mapper.update:
UPDATE CRMADMIN.TBS_COUPON
   SET NO_COUPON_ORDER = #{NO_COUPON_ORDER}
 WHERE CD_COUPON = #{CD_COUPON}
   AND TO_CHAR(DT_EXPIRY_START,'YYYY-MM-DD') = #{DT_EXPIRY_START}
   AND TO_CHAR(DT_EXPIRY_END,'YYYY-MM-DD') = #{DT_EXPIRY_END}

mapper.insert:
SELECT SEQ_TBS_DAUPIN_REQ.NEXTVAL FROM DUAL
INSERT INTO CRMADMIN.TBS_DAUPIN_REQ ( NO_DAUPIN_REQ /* 다우핀 발행요청 PK */ , NO_REQ /* 쿠폰계약번호 */ , CD_COUPON /* 쿠폰PK */ , CNT_PIN /* 발행 요청 핀수 */ , VALID_START /* 쿠폰유효기간 시작일 */ , VALID_END /* 쿠폰유효기간 종료일 */ , COOPER_ORDER /* 제휴처 주문번호 */ , DAUPIN_STATUS /* 상태(01:발행 신청,02:발행중,99:완료) */ , DT_REG /* 최초등록일시 */ , ID_REG_USER /* 최초등록자 */ , DT_UPT /* 최근수정일시 */ , ID_UPT_USER /* 최근수정자 */
) SELECT TO_NUMBER(#{NO_DAUPIN_REQ}) /* 다우핀 발행요청 PK */ , NO_REQ /* 쿠폰계약번호 */ , TO_NUMBER(REPLACE(#{CD_COUPON},',','')) /* 쿠폰PK */ , TO_NUMBER(REPLACE(#{CNT_PIN},',','')) /* 발행 요청 핀수 */
, CASE WHEN TP_EXPIRY_DATE = '00' THEN REPLACE(#{DT_EXPIRY_START},'-','') ELSE TO_CHAR(SYSDATE, 'YYYYMMDD')END /* 쿠폰유효기간 시작일 */ , CASE WHEN TP_EXPIRY_DATE = '00' THEN REPLACE(#{DT_EXPIRY_END},'-','') ELSE TO_CHAR(SYSDATE + TP_EXPIRY_DATE, 'YYYYMMDD') END /* 쿠폰유효기간 종료일 */
, #{COOPER_ORDER} /* 제휴처 주문번호 */ , '01' /* 상태(01:발행 신청,02:발행중,99:완료) */ , SYSDATE /* 최초등록일시 */ , #{user.id} /* 최초등록자 */ , SYSDATE /* 최근수정일시 */ , #{user.id} /* 최근수정자 */ FROM CRMADMIN.TBS_COUPON WHERE CD_COUPON = #{CD_COUPON}

mapper.update:
		UPDATE CRMADMIN.TBS_COUPON
		   SET NM_COUPON = #{NM_COUPON}
			 , DS_COUPON = #{DS_COUPON}
			 , CD_COUPON_TARGET = #{CD_COUPON_TARGET}
			 , CD_SALE_POLICY = CASE WHEN #{CD_COUPON_TARGET} = '2' THEN '1'
									 ELSE #{CD_SALE_POLICY} END
			 , VAL_SALE_POLICY = TO_NUMBER(REPLACE(#{VAL_SALE_POLICY},',',''))
			 , VAL_LIMIT_PRICE = TO_NUMBER(REPLACE(#{VAL_LIMIT_PRICE},',',''))
			 , PRODUCT_PK = #{PRODUCT_PK}
			 , PRODUCT_PK_REAL = #{PRODUCT_PK_REAL}
			 , DT_EXPIRY_START = TO_DATE(#{DT_EXPIRY_START}||'000000', 'YYYY-MM-DDHH24MISS')
			 , DT_EXPIRY_END = TO_DATE(#{DT_EXPIRY_END}||' 235959', 'YYYY-MM-DD HH24MISS')
			 , TP_EXPIRY_DATE = #{TP_EXPIRY_DATE}
			 , TP_MEMBER = #{TP_MEMBER}
			 , NM_CUP_MENU = #{NM_CUP_MENU}
			 , REAL_CUP_PRICE = REPLACE(#{REAL_CUP_PRICE},',','')
             , SALE_CUP_PRICE = REPLACE(#{SALE_CUP_PRICE},',','')
             , SALE_CUP_RATE  = REPLACE(#{SALE_CUP_RATE},',','')
			 , FG_PUB = #{FG_PUB}
			 , DT_UPT = SYSDATE
			 , ID_UPT_USER = #{user.id}
		 WHERE CD_COUPON = #{CD_COUPON}

UPDATE CRMADMIN.TBS_COUPON
   SET FG_PUB = 'N'
   	 , DT_UPT = SYSDATE
   	 , ID_UPT_USER = #{user.id}
 WHERE CD_COUPON = #{CD_COUPON}

mapper.sql:
SELECT TC.CD_COUPON , TC.NM_COUPON , TO_CHAR(TC.DT_EXPIRY_START,'YYYY-MM-DD') ||' ~ '|| TO_CHAR(TC.DT_EXPIRY_END,'YYYY-MM-DD') AS DT_EXPIRY , NVL(ALL_CUP.CNT, 0) AS ALL_CUP /* 전체 쿠폰 발급수 */ , NVL(ALL_MEM.CNT, 0) AS ALL_MEM /* 발급회원수 */ , NVL(CUP_A.CNT , 0) AS CNT_A , NVL(CUP_A_M.CNT, 0) AS CNT_A_M , NVL(CUP_N.CNT , 0) AS CNT_N , NVL(CUP_N_M.CNT, 0) AS CNT_N_M FROM CRMADMIN.TBS_COUPON TC LEFT OUTER JOIN (SELECT COUNT(NO_PIN) AS CNT ,CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN GROUP BY CD_COUPON) ALL_CUP ON ALL_CUP.CD_COUPON= TC.CD_COUPON LEFT OUTER JOIN (SELECT COUNT(CD_COUPON) AS CNT, CD_COUPON FROM ( SELECT COUNT(ID_MEMBER) AS CNT , CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN WHERE ID_MEMBER IS NOT NULL AND DT_PUBLISH IS NOT NULL GROUP BY CD_COUPON,ID_MEMBER) GROUP BY CD_COUPON) ALL_MEM ON ALL_MEM.CD_COUPON = TC.CD_COUPON LEFT OUTER JOIN (SELECT COUNT(NO_PIN) AS CNT ,CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN WHERE STATUS != 'N' GROUP BY CD_COUPON,STATUS) CUP_A ON CUP_A.CD_COUPON = TC.CD_COUPON LEFT OUTER JOIN (SELECT COUNT(CD_COUPON) AS CNT, CD_COUPON FROM ( SELECT COUNT(ID_MEMBER) AS CNT ,CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN WHERE STATUS != 'N' AND ID_MEMBER IS NOT NULL GROUP BY CD_COUPON, ID_MEMBER ) GROUP BY CD_COUPON) CUP_A_M ON CUP_A_M.CD_COUPON = TC.CD_COUPON LEFT OUTER JOIN (SELECT COUNT(NO_PIN) AS CNT ,CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN WHERE STATUS = 'N' GROUP BY CD_COUPON) CUP_N ON CUP_N.CD_COUPON = TC.CD_COUPON LEFT OUTER JOIN (SELECT COUNT(CD_COUPON) AS CNT, CD_COUPON FROM ( SELECT COUNT(ID_MEMBER) AS CNT ,CD_COUPON FROM CRMADMIN.TBS_COUPON_PIN WHERE STATUS = 'N' AND ID_MEMBER IS NOT NULL GROUP BY CD_COUPON, ID_MEMBER ) GROUP BY CD_COUPON ) CUP_N_M ON CUP_N_M.CD_COUPON = TC.CD_COUPON WHERE TC.CD_COUPON_OBJ = 4
AND #{DT_END} >= TO_CHAR(DT_EXPIRY_START, 'YYYY-MM-DD')
AND TO_CHAR(DT_EXPIRY_END, 'YYYY-MM-DD') >= #{DT_START}

SELECT TS.NO_STAMP , TS.TP_SET , TS.DS_TITLE , TO_CHAR(TS.DT_APPLY_START, 'YYYY-MM-DD') ||' ~ '||TO_CHAR(TS.DT_APPLY_END, 'YYYY-MM-DD') AS DT_APPLY , NVL(TSR.CNT, 0) ||' 개' AS CNT_ROLE , TS.FG_USE , SU.USER_NM AS NM_USER_REG , TO_CHAR(TS.DT_REG,'YYYY-MM-DD') AS DT_REG FROM CRMADMIN.TBS_STAMP TS LEFT OUTER JOIN (SELECT COUNT(NO_STAMP) AS CNT , NO_STAMP FROM ( SELECT NO_STAMP, ID_MENU FROM CRMADMIN.TBS_STAMP_ROLE GROUP BY NO_STAMP,ID_MENU ) GROUP BY NO_STAMP) TSR ON TS.NO_STAMP = TSR.NO_STAMP LEFT OUTER JOIN CRMADMIN.SYS_USER SU ON SU.USER_ID = TS.ID_REG_USER where FG_DEL = 'N'
AND #{DT_END} >= TO_CHAR(TS.DT_APPLY_START, 'YYYY-MM-DD')
AND TO_CHAR(TS.DT_APPLY_END, 'YYYY-MM-DD') >= #{DT_START}
AND TS.DS_TITLE LIKE '%'||#{DS_TITLE}||'%'
AND TS.FG_USE = #{FG_USE}
ORDER BY TP_SET ASC, DT_REG DESC

mapper.insert:
SELECT SEQ_TBS_COUPON.NEXTVAL FROM DUAL
INSERT INTO CRMADMIN.TBS_COUPON ( CD_COUPON /* 쿠폰코드 */ , CD_COUPON_OBJ /* 대상(딜리버리, 킹오더 등) */ , NM_COUPON /* 쿠폰명 */ , DS_COUPON /* 쿠폰설명 */ , NO_COUPON_ORDER /* 쿠폰순서 */ , TP_EXPIRY_DATE /* 유효기간구분 */ , DT_EXPIRY_START /* 유효기간시작일 */ , DT_EXPIRY_END /* 유효기간종료일 */ , FG_PUB /* 발행여부 */ , CD_GOODS /* 다우상품코드 */ , NM_GOODS /* 다우상품명 */ , IMG_GOODS /* 다우상품이미지 */ , CD_COUPON_TARGET /* 쿠폰대상(금액,상품) */ , CD_SALE_POLICY /* 할인정책 */ , VAL_SALE_POLICY /* 할인금액 */ , VAL_LIMIT_PRICE /* 최소주문금액 */ , PRODUCT_PK /* 할인상품 */ , PRODUCT_PK_REAL /* 실제판매상품 */ , TP_MEMBER /* 대상회원구분 */ , DT_REG /* 최초등록일시 */ , ID_REG_USER /* 최초등록자 */ , DT_UPT /* 최근수정일시 */ , ID_UPT_USER /* 최근수정자 */ , NO_REQ /* 다우계약번호 */ , NM_CUP_MENU , REAL_CUP_PRICE , SALE_CUP_PRICE , SALE_CUP_RATE
) SELECT #{CD_COUPON_NEW} /* 쿠폰코드 */ , CD_COUPON_OBJ /* 대상(딜리버리, 킹오더 등) */ , NM_COUPON /* 쿠폰명 */ , DS_COUPON /* 쿠폰설명 */ , (SELECT NVL(MAX(NO_COUPON_ORDER),0)+1 FROM CRMADMIN.TBS_COUPON WHERE CD_COUPON_OBJ = TC.CD_COUPON_OBJ AND #{DT_EXPIRY_START} = TO_CHAR(DT_EXPIRY_START,'YYYY-MM-DD') AND #{DT_EXPIRY_END} = TO_CHAR(DT_EXPIRY_END,'YYYY-MM-DD') ) /* 쿠폰순서 */ , TP_EXPIRY_DATE /* 유효기간구분 */ , TO_DATE(#{DT_EXPIRY_START}||'000000', 'YYYY-MM-DDHH24MISS') /* 유효기간시작일 */ , TO_DATE(#{DT_EXPIRY_END}||'235959', 'YYYY-MM-DDHH24MISS') /* 유효기간종료일 */ , 'Y' /* 발행여부 */ , CD_GOODS /* 다우상품코드 */ , NM_GOODS /* 다우상품명 */ , IMG_GOODS /* 다우상품이미지 */ , CD_COUPON_TARGET /* 쿠폰대상(금액,상품) */ , CD_SALE_POLICY /* 할인정책 */ , VAL_SALE_POLICY /* 할인금액 */ , VAL_LIMIT_PRICE /* 최소주문금액 */ , PRODUCT_PK /* 할인상품 */ , PRODUCT_PK_REAL /* 실제판매상품 */ , TP_MEMBER /* 대상회원구분 */ , SYSDATE /* 최초등록일시 */ , #{user.id} /* 최초등록자 */ , SYSDATE /* 최근수정일시 */ , #{user.id} /* 최근수정자 */ , NO_REQ /* 다우계약번호 */ , NM_CUP_MENU , REAL_CUP_PRICE , SALE_CUP_PRICE , SALE_CUP_RATE FROM CRMADMIN.TBS_COUPON TC WHERE CD_COUPON = #{CD_COUPON}

