mapper.select:
SELECT COUNT(*) AS CNT
  FROM CRMADMIN.TBS_STAMP_ROLE TSR
  LEFT OUTER JOIN CRMADMIN.TBS_STAMP TS
    ON TS.NO_STAMP = TSR.NO_STAMP
 WHERE TS.TP_SET = '02'
   AND TS.FG_DEL = 'N'
   AND TSR.ID_MENU = #{CD_MENU}
   AND TSR.CD_GRADE = #{CD_GRADE}
   AND #{DT_APPLY_START} >= TO_CHAR(TS.DT_APPLY_START, 'YYYY-MM-DD')
   AND TO_CHAR(TS.DT_APPLY_END, 'YYYY-MM-DD') >= #{DT_APPLY_END}

mapper.insert:
SELECT CRMADMIN.SEQ_TBS_STAMP_EXCEPT_STORE.NEXTVAL FROM DUAL
INSERT INTO CRMADMIN.TBS_STAMP_EXCEPT_STORE ( NO_STAMP /* 스템프일련번호 */ , NO_STAMP_EXCEPT_STORE /* 스템프제외매장일련번호 */ , ID_STORE /* 매장ID */ , NM_STORE /* 매장명 */ , DT_REG /* 최초등록일시 */ , ID_REG_USER /* 최초등록자 */ , DT_UPT /* 최근수정일시 */ , ID_UPT_USER /* 최근수정자 */
) VALUES ( #{NO_STAMP} /* 스템프일련번호 */ , #{NO_STAMP_EXCEPT_STORE} /* 스템프제외매장일련번호 */ , #{ID_STORE} /* 매장ID */ , #{NM_STORE} /* 매장명 */ , SYSDATE /* 최초등록일시 */ , #{user.id} /* 최초등록자 */ , SYSDATE /* 최근수정일시 */ , #{user.id} /* 최근수정자 */ )

mapper.select:
SELECT CD_MTH_PURCHS || CD_ORDER_CHNN AS CD_MTH_CHNN  /* 구매방법코드 + 주문채널구분코드*/
  FROM CRMADMIN.TBS_STAMP_STND
 WHERE NO_STAMP = #{NO_STAMP}

mapper.update:
update crmadmin.TBS_STAMP
   set DS_TITLE			= #{DS_TITLE}
	 , DT_APPLY_START	= TO_DATE(#{DT_APPLY_START},'YYYY-MM-DD')
	 , DT_APPLY_END		= TO_DATE(#{DT_APPLY_END} || ' 235959', 'YYYY-MM-DD HH24MISS')
	 , FG_USE			= #{FG_USE_MAIN}
	 , DT_UPT			= SYSDATE
	 , ID_UPT_USER		= #{user.id}
 WHERE NO_STAMP = #{NO_STAMP}

mapper.insert:
SELECT CRMADMIN.SEQ_TBS_STAMP_RECODE.NEXTVAL FROM DUAL
INSERT INTO CRMADMIN.TBS_STAMP_RECODE ( NO_STAMP_RECODE , NO_STAMP , TP_SET , DS_TITLE , DT_APPLY_START , DT_APPLY_END , FG_DEL , FG_USE , DT_DEL , DT_REG , ID_REG_USER , DT_UPT , ID_UPT_USER
) SELECT #{NO_STAMP_RECODE} , NO_STAMP , TP_SET , DS_TITLE , DT_APPLY_START , DT_APPLY_END , FG_DEL , FG_USE , DT_DEL , DT_REG , ID_REG_USER , DT_UPT , ID_UPT_USER FROM CRMADMIN.TBS_STAMP WHERE NO_STAMP = #{NO_STAMP}

mapper.select:
SELECT CD_MTH_PURCHS || CD_ORDER_CHNN AS CD_MTH_CHNN  /* 구매방법코드 + 주문채널구분코드*/
  FROM CRMADMIN.TBS_STAMP_STND_RECODE
 WHERE NO_STAMP_RECODE = #{NO_STAMP_RECODE}

mapper.update:
UPDATE CRMADMIN.TBS_STAMP
   SET FG_DEL = 'Y'
   	 , DT_DEL = SYSDATE
   	 , DT_UPT = SYSDATE
   	 , ID_UPT_USER = #{user.id}
 WHERE NO_STAMP = #{NO_STAMP}

mapper.insert:

	SELECT CRMADMIN.SEQ_TBS_STAMP_REWARD.NEXTVAL FROM DUAL

INSERT INTO CRMADMIN.TBS_STAMP_REWARD
