	  NO_TBS_STAMP_REWARD
	, CNT_STAMP_REWARD
	, CD_COUPON
	, NM_COUPON
	, ORDER_NO
	, FG_USE
	, DT_REG
	, ID_REG_USER
	, DT_UPT
	, ID_UPT_USER
)
VALUES
(
	  #{NO_TBS_STAMP_REWARD}
	, #{CNT_STAMP_REWARD}
	, #{CD_COUPON}
	, #{NM_COUPON}
	, #{ORDER_NO}
	, 'Y'
	, SYSDATE
	, #{user.id}
