mapper.insert:
INSERT INTO SYS_RESOURCE (
	   RES_ID
	 , RES_TP
	 , TABLE_NM
	 , COLUMN_NM
	 , RES_NM
	 , REG_DT
	 , USER_ID_REG
	 , UPD_DT
	 , USER_ID_UPD
) VALUES (
	   #{RES_ID}
	 , #{RES_TP}
	 , #{TABLE_NM}
	 , #{COLUMN_NM}
	 , #{RES_NM}
	 , CURRENT_TIMESTAMP
	 , #{user.id}
	 , CURRENT_TIMESTAMP
	 , #{user.id}
)

INSERT INTO SYS_RESOURCE_DTL (
	   RES_ID
	 , LANG_TP
	 , RES_NM
	 , REG_DT
	 , USER_ID_REG
	 , UPD_DT
	 , USER_ID_UPD
) VALUES (
	   #{RES_ID}
	 , #{LANG_TP}
	 , #{RES_NM}
	 , CURRENT_TIMESTAMP
	 , #{user.id}
	 , CURRENT_TIMESTAMP
	 , #{user.id}
)

mapper.update:
UPDATE SYS_RESOURCE
   SET RES_NM		= #{RES_NM}
   	 , TABLE_NM		= #{TABLE_NM}
   	 , COLUMN_NM	= #{COLUMN_NM}
     , UPD_DT		= CURRENT_TIMESTAMP
     , USER_ID_UPD	= #{user.id}
 WHERE RES_ID = #{RES_ID}
