	SELECT SEQ_TBS_DISPATCH_RUN.NEXTVAL FROM DUAL

	
						
insert into TBS_DISPATCH_RUN (
	  ID_DISPATCH_RUN
	, TP_BIZ
	, ID_BIZ_KEY
	, TP_DISPATCH_METHOD
	, ID_EMP_DISPATCH
	, ID_DEPT_DISPATCH
	, DS_EMAIL_SEND
	, ID_PHONE_SEND
	, DS_TITLE_DISPATCH
	, DS_CONTENT
	, DS_DISPATCH
	, DT_DISPATCH_OCCUR
	, DT_DISPATCH_PLAN
	, DS_URL
	, FG_DEL
	, TP_STATUS_DISPATCH
	, TP_DISPATCH_AUTO
	, ID_EMP_REG
	, ID_DEPT_REG
	, DT_REG
	, ID_EMP_UPT
	, ID_DEPT_UPT
	, DT_UPT
	, CD_DISPATCH_BIZ
)
VALUES (
	  #{ID_DISPATCH_RUN}
	, '01'  
	, CASE 
		WHEN #{workCategory} = 'VOC' THEN REPLACE(#{workKeys}, '|', '')  
		WHEN #{workCategory} = 'REQUEST' THEN (SELECT ID_VOC || ID_SUB_VOC FROM TBS_VOC_REQUEST WHERE SEQ_VOC_REQ=#{workKeys} AND ROWNUM=1)
		WHEN #{workCategory} = 'APPLY' THEN (SELECT ID_VOC || ID_SUB_VOC FROM TBS_VOC_APPLY WHERE SEQ_VOC_APPL=#{workKeys} AND ROWNUM=1)
		ELSE #{workKeys}
	  END	
	, #{channelType}
	, #{sender.employeeId}
	, #{sender.departmentId}
	, #{sender.email}
	, #{sender.phoneNumber}
	, #{title}
	, #{message}
	, #{dsDispatch}
	, #{occuredDate}
	, #{planDate}
	, #{url}
	, 'N'
	, 'S0'
	, '01'
	, #{user.id}
	, #{user.partCode}
	, SYSDATE
	, #{user.id}
	, #{user.partCode}
	, SYSDATE
	, #{cdDispatchBiz}
)


	
		SELECT SEQ_TBS_DISPATCH_RUN_TARGET.NEXTVAL FROM DUAL
	
			
