mapper.select:
select M.ID_MESSAGE_RULE , M.TP_BIZ , M.TP_EVENT , M.TP_DISPATCH_METHOD , M.DS_DESC , ST.DS_TITLE DS_TITLE_MESSAGE , ST.DS_CONTENT , M.DS_URL , M.ID_TEMPLATE , M.TP_MESSAGE_SENDER , M.ID_EMP_SEND , M.TP_MESSAGE_TARGET , M.ID_EMP_TARGET , M.ID_ROLE_TARGET , M.ID_DEPT_TARGET , M.DS_CONDITION1 , M.DS_CONDITION2 , M.DS_CONDITION3 , M.DS_CONDITION4 , M.DS_CONDITION5 , M.DS_CONDITION6 , M.FG_USE , M.FG_DEL , M.ID_EMP_REG , M.ID_DEPT_REG , M.DT_REG , M.ID_EMP_UPD , M.ID_DEPT_UPD , M.DT_UPD , M.CD_DISPATCH_BIZ from TBS_MESSAGE_RULE M , SYS_TEMPLATE ST where M.FG_DEL = 'N' AND M.ID_MESSAGE_RULE = ST.CD_NOTIFICATION(+)
and M.TP_BIZ = #{TP_BIZ}
and M.TP_EVENT = #{TP_EVENT}
and M.FG_USE = #{FG_USE}
and M.TP_DISPATCH_METHOD = #{TP_DISPATCH_METHOD}
and M.DS_TITLE_MESSAGE LIKE '%'|| #{DS_TITLE_MESSAGE} ||'%'
ORDER BY M.TP_BIZ, M.TP_EVENT, M.TP_MESSAGE_TARGET DESC

mapper.sql:
select M.ID_MESSAGE_RULE , M.TP_BIZ , M.TP_EVENT , M.TP_DISPATCH_METHOD , M.DS_DESC , M.DS_TITLE_MESSAGE , M.DS_CONTENT , M.DS_URL , M.ID_TEMPLATE , M.TP_MESSAGE_SENDER , M.ID_EMP_SEND , M.TP_MESSAGE_TARGET , M.ID_EMP_TARGET , M.ID_ROLE_TARGET , M.ID_DEPT_TARGET , M.DS_CONDITION1 , M.DS_CONDITION2 , M.DS_CONDITION3 , M.DS_CONDITION4 , M.DS_CONDITION5 , M.DS_CONDITION6 , M.FG_USE , M.FG_DEL , M.ID_EMP_REG , M.ID_DEPT_REG , M.DT_REG , M.ID_EMP_UPD , M.ID_DEPT_UPD , M.DT_UPD from TBS_MESSAGE_RULE M where M.FG_DEL = 'N'
and M.TP_BIZ = #{TP_BIZ}
and M.TP_EVENT = #{TP_EVENT}
and M.FG_USE = #{FG_USE}
and M.TP_DISPATCH_METHOD = #{TP_DISPATCH_METHOD}
and M.DS_TITLE_MESSAGE LIKE '%'|| #{DS_TITLE_MESSAGE} ||'%'
ORDER BY 1 DESC
