mapper.sql:
SELECT ROLE_ID , ROLE_ID AS ROLE_NM , G.GRP_MAP_ID_1 AS ID_COMP , C.NM_COMP AS NM_COMP , G.GRP_MAP_ID_2 AS ID_CHN , G.GRP_MAP_ID_2 AS NM_CHN , COUNT ( * ) AS USR_CNT FROM AP_ROLE_GROUP G, SYS_COMPANY C WHERE G.GRP_MAP_ID_1 = C.CD_COMP(+)
AND G.GRP_MAP_ID_1 = #{CD_BRAND}
AND ROLE_ID = #{CD_ROLE}
AND G.GRP_MAP_ID_2 = #{TP_CHNN_SYSTEM}
GROUP BY ROLE_ID, GRP_MAP_ID_1,C.NM_COMP , GRP_MAP_ID_2 ORDER BY ROLE_ID, GRP_MAP_ID_1,C.NM_COMP , GRP_MAP_ID_2 ASC

mapper.select:
SELECT ROLE_GRP_ID, ROLE_ID, GRP_MAP_ID_1, (SELECT NM_COMP FROM SYS_COMPANY WHERE CD_COMP = GRP_MAP_ID_1) as NM_COMP, GRP_MAP_ID_2, USER_ID, DESCRIPTION, REG_USER_ID, REG_DATETIME, UPD_USER_ID, UPD_DATETIME FROM AP_ROLE_GROUP WHERE 1 = 1
AND ROLE_GRP_ID = #{ROLE_GRP_ID}
AND ROLE_ID = #{ROLE_ID}
AND GRP_MAP_ID_1 = #{GRP_MAP_ID_1}
AND GRP_MAP_ID_2 = #{GRP_MAP_ID_2}

SELECT ROLE_GRP_ID, ROLE_ID, GRP_MAP_ID_1, GRP_MAP_ID_2, G.USER_ID, DESCRIPTION, REG_USER_ID, REG_DATETIME, UPD_USER_ID, UPD_DATETIME, USR.USER_ID, USR.USER_NM, SRT.DEPT_NM, USR.EMP_NO, (SELECT CODE_NAME FROM SYS_CODE WHERE CODE_CATEGORY_ID = 'POSITION_CD' AND CODE = USR.POSITION_CD) POSITION_NM, (SELECT CODE_NAME FROM SYS_CODE WHERE CODE_CATEGORY_ID = 'INCUMBENT_USER_CD' AND CODE = USR.INCUMBENT_CD) INCUMBENT_CD FROM AP_ROLE_GROUP G, SYS_USER USR, SYS_DEPARTMENT SRT WHERE 1 = 1 AND G.USER_ID = USR.USER_ID(+) AND USR.DEPT_ID = SRT.DEPT_ID
AND ROLE_ID = #{ROLE_ID}
AND GRP_MAP_ID_1 = #{GRP_MAP_ID_1}
AND GRP_MAP_ID_2 = #{GRP_MAP_ID_2}

mapper.update:
       		   	DELETE FROM AP_ROLE_GROUP
       		     WHERE ROLE_ID = #{ROLE_ID}
		  	       AND GRP_MAP_ID_1 = #{GRP_MAP_ID_1}
		  	     
				   AND GRP_MAP_ID_2 = #{GRP_MAP_ID_2}
