mapper.select:
        SELECT DS_MESSAGE
		     , FG_USE
		  FROM SYS_BOARD_MESSAGE_INFO
		 WHERE ID_BOARD = #{ID_BOARD}
		   AND TP_MESSAGE = #{TP_MESSAGE}

        SELECT ID_REG_EMP 
		  FROM SYS_BOARD_ARTICLE 
		 WHERE ID_BOARD = #{ID_BOARD}
		   AND ID_ARTICLE = #{ID_ARTICLE}
		   AND ID_SUB = #{ID_SUB}
