mapper.sql:
SELECT TVM.ID_VOC , TO_CHAR(DT_VOC_REG,'YYYY-MM-DD') AS DT_VOC_REG , TO_CHAR(DT_REG,'YYYY-MM-DD HH24:MI:SS') AS DT_REG , TVM.BIZ_CHNN , TVM.NM_CUST , TVM.DS_EMAIL , TVM.NO_PHONE , TVM.DS_TITLE , TVM.CD_ACT_CAUSE , TVM.CD_VOC_STATUS AS CD_STATUS , CASE WHEN SU.USER_NM IS NULL AND DT_VOC_ACT IS NOT NULL THEN '콜센터' ELSE SU.USER_NM END AS NM_ACT_EMP , TO_CHAR(TVM.DT_VOC_ACT, 'YYYY-MM-DD') AS DT_VOC_ACT , CAUSE1.CODE_NAME ||' > '|| CAUSE2.CODE_NAME AS NM_VOC_CAUSE , TVM.FG_MEMBER , MST.STOR_NM AS NM_STORE FROM CRMADMIN.TBS_VOC_MASTER TVM LEFT OUTER JOIN CRMADMIN.SYS_USER SU ON SU.USER_ID = TVM.ID_ACT_EMP LEFT OUTER JOIN CRMADMIN.SYS_CODE CAUSE1 ON CAUSE1.CODE = SUBSTR(TVM.CD_VOC_CAUSE,0,2) AND CAUSE1.CODE_CATEGORY_ID = 'CD_VOC_CAUSE_1' LEFT OUTER JOIN CRMADMIN.SYS_CODE CAUSE2 ON CAUSE2.CODE = TVM.CD_VOC_CAUSE AND CAUSE2.CODE_CATEGORY_ID = 'CD_VOC_CAUSE_2' LEFT OUTER JOIN OMNI.OMNI_STOR_APP MST ON MST.STOR_CD = TVM.ID_STORE AND MST.CMP_CD = 'BKR' AND MST.SALES_ORG_CD = '1000' AND MST.STOR_STATUS = '1' AND SUBSTR(MST.STOR_CD, 4, 1) != 9 AND TO_CHAR(SYSDATE, 'YYYYMMDD') >= MST.SALES_START_DATE WHERE TVM.FG_DEL = 'N' AND TVM.CD_VOC_STATUS != '03'
AND TVM.CD_VOC_CAUSE = #{CD_VOC_CAUSE_2}
AND TVM.CD_VOC_CAUSE LIKE #{CD_VOC_CAUSE_1}||'%'
AND TVM.NM_CUST = #{NM_MEMBER}
AND TVM.NO_PHONE = #{NO_PHONE}
AND UPPER(TVM.DS_EMAIL) LIKE '%'|| UPPER(#{DS_EMAIL}) ||'%'
AND TVM.DS_TITLE LIKE '%'|| #{DS_TITLE} ||'%'
AND TVM.BIZ_CHNN = #{CD_BIZ_CHNN}
AND TVM.BIZ_CHNN IN
#{CD_CHNN}
AND TVM.CD_ACT_CAUSE = #{CD_ACT_CAUSE}
AND TVM.DT_REG >= TO_DATE(#{DT_REG_START}||' 000000','YYYY-MM-DD HH24MISS')
AND TO_DATE(#{DT_REG_END}||' 235959','YYYY-MM-DD HH24MISS') >= TVM.DT_REG
AND TVM.DT_VOC_ACT >= TO_DATE(#{DT_ACT_START}||' 000000','YYYY-MM-DD HH24MISS')
AND TO_DATE(#{DT_ACT_END}||' 235959','YYYY-MM-DD HH24MISS') >= TVM.DT_VOC_ACT
AND TVM.CD_VOC_STATUS = #{CD_VOC_STATUS}
AND TVM.ID_VOC LIKE UPPER('%'||#{ID_VOC}||'%')
AND MST.STOR_NM LIKE '%'||#{NM_STORE}||'%'
AND TVM.ID_STORE = #{ID_STORE}
AND SU.USER_NM LIKE '%'||#{NM_USER}||'%'
AND TVM.ID_ACT_EMP = #{ID_USER}
ORDER BY TVM.DT_REG DESC

mapper.update:
UPDATE CRMADMIN.TBS_VOC_MASTER
   SET CD_VOC_CAUSE = #{CD_VOC_CAUSE_2}
   	 , CD_ACT_CAUSE = #{CD_ACT_CAUSE}
   	 , DS_ACT_CONT = #{DS_ACT_CONT}
   	 , DS_DISPATCH_CONT = #{DS_DISPATCH_CONT}
   	 , FG_SEND = #{FG_SEND}
 
 	 , CD_VOC_STATUS = '02'
 	 , FG_ACT_CRM = 'Y'
   	 , DT_VOC_ACT = SYSDATE
   	 , ID_ACT_EMP = #{user.id}
 
 WHERE ID_VOC = #{ID_VOC}

UPDATE CRMADMIN.TBS_VOC_MASTER
   SET FG_DEL = 'Y'
     , DT_UPT = SYSDATE
     , ID_UPT_USER = #{user.id}
 WHERE ID_VOC = #{ID_VOC}

mapper.insert:
INSERT INTO CRMADMIN.TBS_VOC_MASTER
(
	  ID_VOC                   /* VOC ID */
	, BIZ_CHNN                 /* 접수채널          */
	, ID_BIZ_KEY               /* 업무별ID         */
	, ORDER_NO                 /* 주문번호          */
	, ID_STORE                 /* 매장ID          */
	, FG_MEMBER                /* 회원여부          */
	, ID_MEMBER                /* 회원PK          */
	, NM_CUST                  /* 이름            */
	, NO_PHONE                 /* 연락처           */
	, DS_EMAIL                 /* 이메일           */
	, YN_PRIVACY_AGREE         /* 개인정보수집동의여부    */
	, YN_SENSITIVE_AGREE       /* 민감정보수집동의여부    */
	, YN_PRIVACY_ORDER_AGREE   /* 개인정보제3자제공동의여부 */
	, YN_SENSITIVE_ORDER_AGREE /* 민감정보제3자제공동의여부 */
	, CD_VOC_STATUS            /* VOC상태  */
	, CD_VOC_CAUSE             /* VOC접수유형*/
	, DS_TITLE                 /* 제목     */
	, DS_CONT                  /* 내용     */
	, FG_DEL                   /* 삭제여부   */
	, DT_REG                   /* 최초등록일시 */
	, ID_REG_USER              /* 최초등록자  */
	, DT_UPT                   /* 최근수정일시 */
	, ID_UPT_USER              /* 최근수정자  */
	, DT_VOC_REG               /* VOC 발생일*/
	, MENU_NM                  /* 메뉴 명  */
	, PAYMENT_TYPE             /* 결제 방법  */
	, PAYMENT_PRC              /* 결제 금액  */
	, DT_ORDER                 /* 주문일자   */
	, CD_STATUS				   /* 회원상태          */
	, POS_ORDER_NO             /* POS 주문번호  */
	, OMNI_ORDER_NO            /* OMNI 주문번호 */
	, CALL_ORDER_NO            /* CALL 주문번호 */
	
)
VALUES
(
	  #{ID_VOC}
	, #{BIZ_CHNN}
	, #{ID_BIZ_KEY}
	, #{ORDER_NO}
	, #{STOR_CD}
	, #{FG_MEMBER}
	, #{ID_MEMBER}
	, #{NM_CUST}
	, #{NO_PHONE}
	, #{DS_EMAIL}
	, 'Y'
	, 'Y'
	, 'Y'
	, 'Y'
	, '01'
	, #{CD_VOC_CAUSE}
	, #{DS_TITLE}
	, #{DS_CONT}
	, 'N'
	, SYSDATE
	, #{user.id}
	, SYSDATE
	, #{user.id}
	, TO_DATE(#{DT_VOC_REG}, 'YYYY-MM-DD')
	, #{MENU_NM}
	, #{PAYMENT_TYPE}
	, REPLACE(#{PAYMENT_PRC},',','')
	, TO_DATE(#{DT_ORDER}, 'YYYY-MM-DD HH24:MI:SS')
	, #{CD_STATUS}
	, #{POS_ORDER_NO}
	, #{ORDER_NO}
	, #{CALL_ORDER_NO}
)

mapper.sql:
SELECT STOR_CD , STOR_NM , TEL_NO , ADDR_1 , ADDR_2 , DECODE(DLVYN,'0','N','Y') AS DLVYN , DECODE(KORD_YN,'0','N','Y') AS KORD_YN , DRT_FRCS_TYPE , CASE WHEN DIRVE_TH = '1' THEN 'Y' ELSE 'N' END DIRVE_TH , STOR_STATUS AS OPER_TYPE FROM OMNI.OMNI_STOR_APP MST WHERE 1=1 AND MST.CMP_CD = 'BKR' AND MST.SALES_ORG_CD = '1000' AND MST.STOR_STATUS = '1' AND SUBSTR(MST.STOR_CD, 4, 1) != 9 AND TO_CHAR(SYSDATE, 'YYYYMMDD') >= MST.SALES_START_DATE
AND STOR_CD LIKE '%'|| #{STOR_CD} ||'%'
AND STOR_NM LIKE '%'|| #{STOR_NM} ||'%'
AND REPLACE(TEL_NO,'-','') LIKE '%'||REPLACE(#{TEL_NO},'-','')||'%'
AND DLVYN = DECODE(#{DLVYN},'Y','1','0')
AND KORD_YN = DECODE(#{KORD_YN},'Y','1','0')
AND DRT_FRCS_TYPE = #{DRT_FRCS_TYPE}
AND STOR_STATUS = #{CLOSE}
AND DIRVE_TH = #{YN_FLAG}
ORDER BY STOR_NM ASC

mapper.select:
SELECT ROWNUM AS "순번" , TO_CHAR(TVM.DT_REG,'YYYY-MM-DD') AS "날짜" , TO_CHAR(TVM.DT_REG,'HH24:MI') AS "시간" , MSTD1.DISTRCT_NM AS "광역" , MSTD2.DISTRCT_NM AS "지역" , MST.STOR_NM AS "매장명" , '' AS "점장" , '' AS "Shift MGR" , CASE WHEN CAUSE2.CODE = '02001' THEN 'S분류' WHEN CAUSE2.CODE = '02002' OR CAUSE2.CODE = '02003' OR CAUSE2.CODE = '02004' OR CAUSE2.CODE = '02005' THEN '1분류' WHEN CAUSE2.CODE = '02006' OR CAUSE2.CODE = '02007' THEN '2분류' ELSE '3분류' END AS "분류" , CAUSE1.CODE_NAME ||' > '|| CAUSE2.CODE_NAME AS "구분" , TVM.NM_CUST AS "고객명" , TVM.NO_PHONE AS "연락처" , TVM.DS_EMAIL AS "이메일" , TVM.DS_TITLE AS "제목" , TVM.DS_CONT AS "내용" , TVM.DS_ACT_CONT AS "처리내용" , VRCD.CODE_NAME AS "처리상태" , TVM.DS_DISPATCH_CONT AS "통보내용" , '' AS "비고" FROM CRMADMIN.TBS_VOC_MASTER TVM INNER JOIN CRMADMIN.SYS_CODE CAUSE2 ON TVM.CD_VOC_CAUSE = CAUSE2.CODE AND CAUSE2.CODE_CATEGORY_ID = 'CD_VOC_CAUSE_2' INNER JOIN CRMADMIN.SYS_CODE CAUSE1 ON CAUSE2.PARENT_CODE = CAUSE1.CODE AND CAUSE1.CODE_CATEGORY_ID = 'CD_VOC_CAUSE_1' LEFT OUTER JOIN OMNI.MST_STOR MST ON MST.STOR_CD = TVM.ID_STORE AND MST.CMP_CD = 'BKR' AND MST.SALES_ORG_CD = '1000' AND MST.OPER_TYPE <> '09' LEFT OUTER JOIN OMNI.MST_DISTRCT MSTD1 ON MST.DISTRCT_CD_1 = MSTD1.DISTRCT_CD AND MSTD1.CMP_CD = 'BKR' AND MSTD1.SALES_ORG_CD = '1000' LEFT OUTER JOIN OMNI.MST_DISTRCT MSTD2 ON MST.DISTRCT_CD_2 = MSTD2.DISTRCT_CD AND MSTD2.CMP_CD = 'BKR' AND MSTD2.SALES_ORG_CD = '1000' LEFT OUTER JOIN CRMADMIN.SYS_CODE VRCD ON TVM.CD_VOC_STATUS = VRCD.CODE AND VRCD.CODE_CATEGORY_ID = 'CD_VOC_STATUS' WHERE TVM.FG_DEL = 'N' AND TVM.CD_VOC_STATUS <> '03'
AND TVM.CD_VOC_CAUSE = #{CD_VOC_CAUSE_2}
AND TVM.CD_VOC_CAUSE LIKE #{CD_VOC_CAUSE_1}||'%'
AND TVM.NM_CUST = #{NM_MEMBER}
AND TVM.NO_PHONE = #{NO_PHONE}
AND UPPER(TVM.DS_EMAIL) LIKE '%'|| UPPER(#{DS_EMAIL}) ||'%'
AND TVM.DS_TITLE LIKE '%'|| #{DS_TITLE} ||'%'
AND TVM.BIZ_CHNN = #{CD_BIZ_CHNN}
AND TVM.BIZ_CHNN IN
#{CD_CHNN}
AND TVM.CD_ACT_CAUSE = #{CD_ACT_CAUSE}
AND TVM.DT_REG >= TO_DATE(#{DT_REG_START}||' 000000','YYYY-MM-DD HH24MISS')
AND TO_DATE(#{DT_REG_END}||' 235959','YYYY-MM-DD HH24MISS') >= TVM.DT_REG
AND TVM.DT_VOC_ACT >= TO_DATE(#{DT_ACT_START}||' 000000','YYYY-MM-DD HH24MISS')
AND TO_DATE(#{DT_ACT_END}||' 235959','YYYY-MM-DD HH24MISS') >= TVM.DT_VOC_ACT
AND TVM.CD_VOC_STATUS = #{CD_VOC_STATUS}
AND TVM.ID_VOC LIKE UPPER('%'||#{ID_VOC}||'%')
AND MST.STOR_NM LIKE '%'||#{NM_STORE}||'%'
AND TVM.ID_STORE = #{ID_STORE}
AND SU.USER_NM LIKE '%'||#{NM_USER}||'%'
AND TVM.ID_ACT_EMP = #{ID_USER}
ORDER BY TVM.DT_REG DESC
